INSERT INTO recetas (rect_id, rect_nombre, rect_categoria, rest_preparacion, rect_img, rect_estado) VALUES (1, 'Bistec a la parrilla', 'Carnes', 'Sazona los bistecs con sal y pimienta. Calienta la parrilla a fuego medio-alto. Asa los bistecs durante 3-4 minutos por cada lado. Retira los bistecs de la parrilla y déjalos reposar durante 5 minutos antes de servir.', 1, true), (2, 'Estofado de carne', 'Carnes', 'Calienta aceite de oliva en una olla grande. Agrega la carne y dórala por todos lados. Añade el ajo, la cebolla y las hierbas aromáticas. Vierte el vino tinto y deja que se evapore. Agrega agua, sal y pimienta. Cocina a fuego lento durante 2-3 horas hasta que la carne esté tierna.', 2, true), (3, 'Brochetas de carne y verduras', 'Carnes', 'Corta la carne en trozos y las verduras en cubos. Ensarta los trozos de carne y las verduras en brochetas. Sazona con sal, pimienta, ajo y hierbas al gusto. Asa las brochetas en la parrilla durante 10-15 minutos, volteándolas ocasionalmente. Sirve las brochetas calientes.', 3, true);
