INSERT INTO recetas (rect_id, rect_nombre, rect_categoria, rest_preparacion, rect_img, rect_estado) VALUES (4, 'Brownies de almendra', 'Dulces', 'Precalienta el horno a 180°C. Mezcla la harina de almendra, el cacao en polvo y la miel. Agrega la mantequilla de maní y mezcla bien. Incorpora los dátiles y las nueces picadas. Vierte la mezcla en un molde y hornea durante 20-25 minutos. Deja enfriar antes de cortar en porciones.', 1, true), (5, 'Galletas de avena y plátano', 'Dulces', 'Tritura los plátanos maduros en un recipiente. Agrega la harina de avena y mezcla hasta obtener una masa homogénea. Añade las nueces picadas y el aceite de coco. Forma pequeñas galletas y colócalas en una bandeja. Hornea durante 15-20 minutos a 180°C. Deja enfriar antes de disfrutar.', 2, true),(6, 'Trufas de cacao y nueces', 'Dulces', 'Mezcla el cacao en polvo, las nueces trituradas y la miel en un recipiente. Forma pequeñas bolitas con la mezcla. Refrigera las trufas durante al menos 1 hora. Sirve frías y disfruta de estas deliciosas trufas.', 3, true);
INSERT INTO recetas_ingredientes (rect_id, ingr_id) VALUES (4, 10), (4, 12), (4, 13), (4, 14), (4, 15), (4, 16), (5, 11), (5, 17), (5, 16), (5, 18), (6, 12), (6, 16), (6, 13);
INSERT INTO recetas (rect_id, rect_nombre, rect_categoria, rest_preparacion, rect_img, rect_estado) VALUES (7, 'Sopa de verduras', 'Sopas', 'En una olla, calienta el caldo de verduras. Agrega la cebolla, el ajo, la zanahoria y el apio picados. Cocina a fuego medio durante 10 minutos. Añade el brócoli, las espinacas y el pimiento rojo. Cocina por otros 5 minutos hasta que las verduras estén tiernas. Sirve caliente y disfruta de esta deliciosa sopa saludable.', 1, true), (8, 'Sopa de lentejas', 'Sopas', 'En una olla, cocina las lentejas en agua hirviendo hasta que estén tiernas. En otra olla, saltea la cebolla y el ajo picados en un poco de aceite. Agrega las lentejas cocidas y el caldo de verduras. Cocina a fuego medio durante 15-20 minutos. Condimenta al gusto con sal, pimienta y especias. Sirve caliente y disfruta de esta reconfortante sopa de lentejas.', 2, true), (9, 'Sopa cremosa de brócoli', 'Sopas', 'En una olla, cocina el brócoli en agua hirviendo hasta que esté tierno. En otra olla, saltea la cebolla y el ajo picados en un poco de aceite. Agrega el brócoli cocido y el caldo de verduras. Cocina a fuego medio durante 10 minutos. Tritura la sopa con una licuadora o procesadora de alimentos hasta obtener una consistencia cremosa. Sirve caliente y disfruta de esta deliciosa sopa cremosa de brócoli.', 3, true);
