INSERT INTO recetas (rect_id, rect_nombre, rect_categoria, rest_preparacion, rect_img, rect_estado) VALUES (10, 'Ensalada César', 'Ensaladas', 'Lava y corta la lechuga en trozos. Agrega tomate, pepino y zanahoria picados. Añade aceitunas, pimiento rojo y cebolla morada en rodajas. Prepara un aderezo mezclando aceite de oliva, vinagre balsámico, sal y pimienta al gusto. Vierte el aderezo sobre la ensalada y mezcla bien. Sirve y disfruta de esta refrescante ensalada César.', 1, true), (11, 'Ensalada Caprese', 'Ensaladas', 'Corta tomates y mozzarella en rodajas. Lava y corta hojas de albahaca fresca. Alterna capas de tomate, mozzarella y albahaca en un plato. Rocía con aceite de oliva y vinagre balsámico. Espolvorea con sal y pimienta al gusto. Sirve y disfruta de esta deliciosa ensalada Caprese.', 2, true), (12, 'Ensalada Mediterránea', 'Ensaladas', 'Corta tomates, pepino y cebolla en rodajas. Agrega aceitunas y pimiento rojo picados. Mezcla todos los ingredientes en un tazón. Prepara un aderezo con aceite de oliva, vinagre balsámico, ajo picado, sal y pimienta. Vierte el aderezo sobre la ensalada y mezcla bien. Sirve y disfruta de esta sabrosa ensalada mediterránea.', 3, true);
