INSERT INTO recetas (rect_id, rect_nombre, rect_categoria, rest_preparacion, rect_img, rect_estado) VALUES (13 ,'Jugo de manzana y jengibre', 'Bebidas', 'Lava y pela las manzanas, luego córtalas en trozos. Exprime el limón y ralla el jengibre. Coloca las manzanas, el limón y el jengibre rallado en una licuadora. Agrega agua y hielo según tu preferencia. Licua todo hasta obtener una mezcla suave y homogénea. Endulza con miel si lo deseas. Sirve y disfruta de este refrescante jugo de manzana y jengibre.', 1, true), (14, 'Batido de pera y menta', 'Bebidas', 'Lava y pela las peras, luego córtalas en trozos. Lava y corta las hojas de menta. Coloca las peras y las hojas de menta en una licuadora. Agrega agua y hielo según tu preferencia. Licua todo hasta obtener una mezcla suave y homogénea. Sirve y disfruta de este refrescante batido de pera y menta.', 2, true), (15, 'Jugo de naranja con jengibre', 'Bebidas', 'Exprime las naranjas para obtener el jugo fresco. Ralla el jengibre y agrégalo al jugo de naranja. Mezcla bien para combinar los sabores. Agrega hielo si lo deseas. Sirve y disfruta de este delicioso jugo de naranja con un toque de jengibre.', 3, true);
INSERT INTO recetas_ingredientes (rect_id, ingr_id) VALUES (13, 35), (13, 38), (13, 39), (13, 41), (13, 42), (13, 13), (14, 36), (14, 40), (14, 41), (14, 13), (15, 37), (15, 39), (15, 41), (15, 13);
INSERT INTO recetas (rect_id, rect_nombre, rect_categoria, rest_preparacion, rect_img, rect_estado) VALUES (16, 'Pan integral', 'Pan', 'Mezcla harina integral, levadura, agua y sal en un tazón grande. Amasa la mezcla hasta obtener una masa suave y elástica. Deja reposar la masa en un lugar cálido durante aproximadamente una hora hasta que duplique su tamaño. Precalienta el horno a una temperatura adecuada. Forma la masa en la forma deseada para el pan. Espolvorea semillas de chía, semillas de girasol, semillas de lino y nueces en la parte superior del pan. Hornea el pan en el horno durante el tiempo especificado. Retira el pan del horno y deja que se enfríe antes de cortarlo y disfrutarlo.', 1, true), (17, 'Pan de centeno', 'Pan', 'Mezcla harina integral de centeno, levadura, agua y sal en un tazón grande. Amasa la mezcla hasta obtener una masa suave y elástica. Deja reposar la masa en un lugar cálido durante aproximadamente una hora hasta que duplique su tamaño. Precalienta el horno a una temperatura adecuada. Forma la masa en la forma deseada para el pan. Espolvorea semillas de chía, semillas de girasol, semillas de lino y nueces en la parte superior del pan. Hornea el pan en el horno durante el tiempo especificado. Retira el pan del horno y deja que se enfríe antes de cortarlo y disfrutarlo.', 2, true), (18, 'Pan de zanahoria y nueces', 'Pan', 'Mezcla harina integral, zanahoria rallada, nueces picadas, levadura, agua, sal y aceite de oliva en un tazón grande. Amasa la mezcla hasta obtener una masa suave y elástica. Deja reposar la masa en un lugar cálido durante aproximadamente una hora hasta que duplique su tamaño. Precalienta el horno a una temperatura adecuada. Forma la masa en la forma deseada para el pan. Espolvorea semillas de chía, semillas de girasol, semillas de lino y nueces en la parte superior del pan. Hornea el pan en el horno durante el tiempo especificado. Retira el pan del horno y deja que se enfríe antes de cortarlo y disfrutarlo.', 3, true);
