INSERT INTO testimonios (testimonio_id, testimonio_fecha, usr_id, testimonio_comentario, testimonio_img, testimonio_estado) VALUES (1, '2022-05-15', 1, '¡Me encanta esta página! Es fácil de usar y encuentro recetas deliciosas para preparar en casa. ¡Altamente recomendada!', 'masculino', true), (2, '2022-06-02', 2, 'La variedad de recetas disponibles es buena. Siempre encuentro algo nuevo.', 'femenino', true), (3, '2022-07-10', 3, 'He mejorado mis habilidades culinarias. Los videos y las instrucciones paso a paso son muy útiles.', 'masculino', true), (4, '2022-08-20', 4, 'Me encanta la variedad de recetas que encuentro. Siempre puedo encontrar algo delicioso para preparar.', 'femenino', true), (5, '2022-09-05', 5, 'Esta página me ha ayudado a mejorar mis habilidades en la cocina. Las recetas son claras y fáciles de seguir.', 'masculino', true), (6, '2022-10-12', 4, 'Recomiendo esta página a todos los amantes de la cocina. Es un buen recurso para encontrar inspiración y aprender nuevas recetas.', 'femenino', true);
